    public void initialize() {
        getHolder().addCallback(this);
        setFocusable(true);
        try{
        	this.m_volume = ImageUtils.loadImage( R.drawable.volume, m_context );
        	this.m_muted = ImageUtils.loadImage( R.drawable.mute, m_context );
        }catch(Exception e){
        	Log.e(TAG, "Could not load volume images: "+e.toString(), e);
        }
        this.loadLevel();
    }

    public void startThread() {
    	if( drawThread == null ){
    		drawThread = new DrawThread(getHolder(), this);
    		drawThread.setRunning(true);
    		drawThread.start();
    	}
    }

    public void stopThread() {
    	if( drawThread != null ){    		
    		drawThread.setRunning(false); 
    		drawThread = null;
    	}
    }

